DROP TABLE IF EXISTS `college`;
CREATE TABLE `college` (
  `college_id` int(32) NOT NULL AUTO_INCREMENT,
  `college_name` varchar(50) NOT NULL,
	`college_location` varchar(25) DEFAULT NULL,
  `art_min_grade` int(15) DEFAULT NULL,
	`art_min_rank` int(15) DEFAULT NULL,
	`science_min_grade` int(15) DEFAULT NULL,
	`science_min_rank` int(15) DEFAULT NULL,
	`desc` varchar(512) DEFAULT NULL,
	`url` varchar(128) DEFAULT NULL,
	`img` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`college_id`)
) ENGINE=InnoDB CHARSET=utf8 ROW_FORMAT=COMPACT;
INSERT INTO college(`college_name`,`college_location`,`art_min_grade`,`art_min_rank`,`science_min_grade`,`science_min_rank`,`desc`,`url`,`img`) VALUES ('玉林师范学院','广西',516,43053,446,135818,'玉林师范学院（Yulin Normal University）位于中国优秀旅游城市、广西东南部的玉林市，是广西壮族自治区直属全日制普通本科高校，是广西4所高等教育综合改革试点高校之一。2016年，入选教育部数据中国“百校工程”合作院校。','https://www.ylu.edu.cn/','https://bkimg.cdn.bcebos.com/pic/d439b6003af33a87e9503dcefa1607385343faf21f87?x-bce-process=image/resize,m_lfit,w_268,limit_1/format,f_jpg'),('右江民族医学院','广西',null,null,483,90567,'右江民族医学院（Youjiang Medical University For Nationalities）简称“右医”，是广西壮族自治区直属的全日制普通本科院校，位于广西壮族自治区百色市，是“卓越医生教育培养计划”入选高校。','http://www.ymcn.gx.cn','https://bkimg.cdn.bcebos.com/pic/7c1ed21b0ef41bd56d72ce2753da81cb39db3d3b?x-bce-process=image/resize,m_lfit,w_268,limit_1/format,f_jpg'),('梧州学院','广西',515,43326,447,135321,'梧州学院（Wuzhou University）位于广西梧州市，是广西壮族自治区人民政府和梧州市人民政府共建的多科性地方全日制普通本科院校，由广西大学对口支援，入选数据中国“百校工程”。','http://www.gxuwz.edu.cn','https://bkimg.cdn.bcebos.com/pic/8718367adab44aedd7da856bbb1c8701a08bfbad?x-bce-process=image/resize,m_lfit,w_268,limit_1/format,f_jpg'),('南宁学院','广西',514,44374,443,140504,'南宁学院（NanNing University，NNU），简称“南院”，是南宁市人民政府与民革广西区委合作共办的高校，为应用技术大学（学院）联盟，系教育部批准可向港澳台招收本科生的学校。','http://www.nnxy.cn/','https://bkimg.cdn.bcebos.com/pic/9825bc315c6034a8d27682dcca134954082376d2?x-bce-process=image/resize,m_lfit,w_268,limit_1/format,f_jpg'),('南宁师范大学','广西',530,30955,462,115227,'南宁师范大学（Nanning Normal University），简称“南宁师大”，坐落于广西壮族自治区南宁市，入选国家中西部高校基础能力建设工程（二期）、新工科研究与实践项目，为全国深化创新创业教育改革示范高校、教育部数据中国“百校工程”项目试点高校、西南大学对口支援建设院校、广西新增博士学位授予单位立项建设单位、广西一流学科建设高校。','http://www.nnnu.edu.cn/','https://bkimg.cdn.bcebos.com/pic/f7246b600c338744ebf80fa24d45cef9d72a61590a91?x-bce-process=image/resize,m_lfit,w_268,limit_1/format,f_jpg'),('柳州工学院','广西',500,57594,427,161335,'柳州工学院创建于2002年，前身为广西科技大学鹿山学院，由柳州市政府与广西科技大学共同举办。2020年4月，学院经教育部批准转设为独立设置的普通全日制本科高校，更名为“柳州工学院”，为国有资产全额出资设立的事业单位。','http://lzls.gxust.edu.cn/','https://bkimg.cdn.bcebos.com/pic/caef76094b36acaf2eddaae728909a1001e9390136dd?x-bce-process=image/resize,m_lfit,w_268,limit_1/format,f_jpg'),('贺州学院','广西',516,43099,349,249960,'贺州学院（Hezhou University）位于广西壮族自治区贺州市，是经教育部批准设立的一所公办全日制普通本科高校。','http://www.hzu.gx.cn','https://bkimg.cdn.bcebos.com/pic/7dd98d1001e939018c0377d67bec54e737d196d1?x-bce-process=image/resize,m_lfit,w_268,limit_1/format,f_jpg'),('河池学院','广西',null,null,446,136359,'河池学院坐落于广西宜州区，是桂西北地区唯一一所普通本科院校。是在原河池师范高等专科学校的基础上，于2003年4月经教育部批准成立的一所全日制普通本科院校。学院实行“自治区与河池市共建，以自治区为主”的办学体制。','http://www.hcnu.edu.cn','https://bkimg.cdn.bcebos.com/pic/35a85edf8db1cb1334a4138dd554564e93584b5a?x-bce-process=image/resize,m_lfit,w_268,limit_1/format,f_jpg'),('桂林医学院','广西',null,null,494,78051,'桂林医学院创建于1935年，前身为广西省立桂林高级助产护士学校，是全国建校历史较早的医科院校之一。1958年学校升格为桂林医学专科学校，1987年升格为桂林医学院，1993年获招收外国留学生资格，2006年获硕士学位授予权，2014年列为博士学位授权立项建设单位，2015年获批设立博士后科研工作站。','http://www.glmc.edu.cn','https://bkimg.cdn.bcebos.com/pic/ac6eddc451da81cbb23989975d66d01608243188?x-bce-process=image/resize,m_lfit,w_268,limit_1/format,f_jpg'),('桂林旅游学院','广西',515,43304,447,135569,'桂林旅游学院（Guilin Tourism University）简称“桂林旅院"，坐落于广西壮族自治区桂林市，学校是联合国世界旅游组织重点支持单位及其教育委员会附属成员单位、广西一流学科（培育）立项建设高校，亚太旅游协会教育类会员单位、中国-东盟旅游人才教育培训基地。','https://www.gltu.edu.cn/','https://bkimg.cdn.bcebos.com/pic/f9dcd100baa1cd11f5147012be12c8fcc3ce2d65?x-bce-process=image/resize,m_lfit,w_268,limit_1/format,f_jpg'),('桂林理工大学','广西',532,29763,486,86577,'桂林理工大学(Guilin university of technology)，简称“桂工”，坐落于世界著名的风景游览城市和历史文化名城——桂林市，中国高校行星科学联盟、“中西部高校基础能力建设工程”重点建设高校、入选教育部卓越工程师教育培养计划、国家建设高水平大学公派研究生项目、“百校工程”、国家大学生文化素质教育基地、国家级新工科研究与实践项目；','http://www.glut.edu.cn','https://bkimg.cdn.bcebos.com/pic/bba1cd11728b47102ab7e5fdc4cec3fdfc032362?x-bce-process=image/resize,m_lfit,w_268,limit_1/format,f_jpg'),('桂林航天工业学院','广西',516,43099,455,124652,'桂林航天工业学院（Guilin University Of Aerospace Technology），位于历史文化名城——桂林市，入选教育部首批新工科研究与实践项目、国家级特色专业建设点， [1]  广西一流学科(培育)建设高校，以工学为主，涵盖理、工、管、文、艺的等多学科协调发展的学科专业体系。','http://www.guat.edu.cn','https://bkimg.cdn.bcebos.com/pic/adaf2edda3cc7cd977d173533401213fb90e91b8?x-bce-process=image/resize,m_lfit,w_268,limit_1/format,f_jpg'),('桂林电子科技大学','广西',537,26028,519,53109,'学校始建于1960年，1980年经国务院批准成立桂林电子工业学院，2006年更名为桂林电子科技大学。学校先后隶属于第四机械工业部、电子工业部、机械电子工业部、中国电子工业总公司、信息产业部。2000年管理体制转为中央与地方共建、以地方管理为主。2008年、2018年先后成为工业和信息化部与广西共建高校、国家国防科技工业局与广西共建高校。','http://www.gliet.edu.cn','https://bkimg.cdn.bcebos.com/pic/00e93901213fb80e43a6638d39d12f2eb9389470?x-bce-process=image/resize,m_lfit,w_268,limit_1/format,f_jpg'),('广西中医药大学','广西',545,21306,495,76568,'广西中医药大学（Guangxi University of Chinese Medicine），坐落于广西壮族自治区首府南宁，由广西壮族自治区人民政府与国家中医药管理局共建，入选国家“双万计划”、中西部高校基础能力建设工程、卓越医生（中医）教育培养计划、广西2011计划、广西一流学科建设高校；','http://www.gxtcmu.edu.cn','https://bkimg.cdn.bcebos.com/pic/b812c8fcc3cec3fd3542648fda88d43f869427c8?x-bce-process=image/resize,m_lfit,w_268,limit_1/format,f_jpg'),('广西艺术学院','广西',519,40274,452,128297,'广西艺术学院（GUANGXI ARTS UNIVERSITY），简称“广艺”，坐落于广西壮族自治区首府南宁。是全国7所综合性普通本科高等艺术院校之一。','http://www.gxau.edu.cn','https://bkimg.cdn.bcebos.com/pic/9c16fdfaaf51f3de395c793c9beef01f3b2979f8?x-bce-process=image/resize,m_lfit,w_268,limit_1/format,f_jpg'),('广西医科大学','广西',null,null,548,30223,'广西医科大学（Guangxi Medical University），坐落于广西南宁市，由国家卫生健康委员会和广西壮族自治区人民政府共建。','http://www.gxmu.edu.cn','https://bkimg.cdn.bcebos.com/pic/4e4a20a4462309f7ef6823f8700e0cf3d6cad6d1?x-bce-process=image/resize,m_lfit,w_268,limit_1/format,f_jpg'),('广西外国语学院','广西',509,48786,429,159200,'广西外国语学院（Guangxi University of Foreign Languages），简称“广外院”，座落于广西壮族自治区首府南宁市，是一所以外国语言文学为主，文学、经济学、管理学、艺术学、教育学、工学等多学科协调发展的全日制民办普通本科高校，由广西民族大学对口支援。','http://www.gxufl.com','https://bkimg.cdn.bcebos.com/pic/1ad5ad6eddc451daa5aa9f82b8fd5266d016327e?x-bce-process=image/resize,m_lfit,w_268,limit_1/format,f_jpg'),('广西师范大学','广西',555,15502,517,54117,'西师范大学（Guangxi Normal University），简称“广西师大（GXNU）”，坐落于世界著名山水旅游名城桂林市，由教育部和广西壮族自治区人民政府共建。','http://www.gxnu.edu.cn','https://bkimg.cdn.bcebos.com/pic/a08b87d6277f9e2f4586fd9f1f30e924b999f3f2?x-bce-process=image/resize,m_lfit,w_268,limit_1/format,f_jpg'),('广西民族大学','广西',543,22213,477,96814,'广西民族大学（GuangXi University for Nationalities）简称“民大”，是国家民族事务委员会和广西壮族自治区人民政府“省部共建”高校，坐落于中国“绿城”南宁市。','http://www.gxun.edu.cn','https://bkimg.cdn.bcebos.com/pic/0e2442a7d933c89537cba9d2d11373f0830200e5?x-bce-process=image/resize,m_lfit,w_268,limit_1/format,f_jpg'),('广西科技师范学院','广西',515,43304,447,135569,'广西科技师范学院（Guangxi Science & Technology Normal University）位于广西壮族自治区来宾市，为广西壮族自治区区属公办高等本科院校，是教育部数据中国“百校工程”首批试点高校，广西新建本科学校整体转型发展试点学校，为广西科技厅和来宾市人民政府共建学校。','http://www.gxstnu.edu.cn/','https://bkimg.cdn.bcebos.com/pic/c8177f3e6709c93d70cf5a20db77efdcd100bba1fe98?x-bce-process=image/resize,m_lfit,w_268,limit_1/format,f_jpg'),('广西科技大学','广西',483,75174,476,98755,'广西科技大学坐落在柳州市，是国务院学位委员会批准的学士、硕士学位授权单位，是“卓越医生教育培养计划”试点高校，是“卓越工程师教育培养计划2.0”入选高校，是“新工科研究与实践项目”入选高校，直属于广西壮族自治区人民政府管理。','http://www.gxust.edu.cn','https://bkimg.cdn.bcebos.com/pic/f703738da9773912c1bf215cfd198618367ae276?x-bce-process=image/resize,m_lfit,w_268,limit_1/format,f_jpg'),('广西大学','广西',544,32988,null,null,'广西大学，坐落于广西首府南宁市，是教育部与广西壮族自治区人民政府“部省合建”高校，首批国家“世界一流学科建设高校”。','http://www.gxu.edu.cn','https://bkimg.cdn.bcebos.com/pic/6609c93d70cf3bc79f3d97f8e94aada1cd11728bfcc7?x-bce-process=image/resize,m_lfit,w_268,limit_1/format,f_jpg'),('广西财经学院','广西',530,30919,480,93327,'广西财经学院（Guangxi University of Finance and Economics）位于广西壮族自治区首府南宁市，是一所区属全日制普通本科院校，为教育部应用型本科高校建设立项建设单位、广西特色高校立项建设单位。','http://www.gxufe.cn','https://bkimg.cdn.bcebos.com/pic/10dfa9ec8a13632762d02391afc5b7ec08fa513d6974?x-bce-process=image/resize,m_lfit,w_268,limit_1/format,f_jpg'),('北海艺术设计学院','广西',496,61302,425,164696,'北海艺术设计学院，简称“北艺”，位于广西北海市。是一所经教育部批准的艺术设计类本科高校。','http://www.sszss.com','https://bkimg.cdn.bcebos.com/pic/5ab5c9ea15ce36d3d5397c3f4cba2d87e950352a1b20?x-bce-process=image/resize,m_lfit,w_268,limit_1/format,f_jpg'),('北部湾大学','广西',517,41705,447,135486,'北部湾大学位于广西壮族自治区钦州市，是一所以工学、理学、管理学为主的全日制普通高等学校，是广西壮族自治区人民政府与国家海洋局共建高校。','https://www.bbgu.edu.cn/','https://bkimg.cdn.bcebos.com/pic/472309f790529822c2238c22d9ca7bcb0a46d43b?x-bce-process=image/resize,m_lfit,w_268,limit_1/format,f_jpg')
